   private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
      // Datos
		int ladoA = Integer.parseInt(txtLadoA.getText());
		int ladoB = Integer.parseInt(txtLadoB.getText());
		int ladoC = Integer.parseInt(txtLadoC.getText());
		// Procesar
		TrianguloController controller = new TrianguloController();
		boolean valido = controller.esValido(ladoA,ladoB,ladoC);
		int perimetro = controller.perimetro(ladoA,ladoB,ladoC);
		double area = controller.area(ladoA,ladoB,ladoC);
		// Reporte
		txtValido.setText(valido?"Es valido":"NO es valido");
		txtPerimetro.setText("" + (perimetro==-1?"":perimetro));
		txtArea.setText("" + (area==-1.0?"":area));
		this.setStatusEdit(false);
   }//GEN-LAST:event_btnProcesarActionPerformed

   private void btnLimpiarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLimpiarActionPerformed
      // Limpia las cajas de texto
		txtLadoA.setText("");
		txtLadoB.setText("");
		txtLadoC.setText("");
		txtValido.setText("");
		txtPerimetro.setText("");
		txtArea.setText("");
		this.setStatusEdit(true);
   }//GEN-LAST:event_btnLimpiarActionPerformed

