	private void setStatusEdit(boolean editStatus) {
		txtLadoA.setEnabled(editStatus);
		txtLadoB.setEnabled(editStatus);
		txtLadoC.setEnabled(editStatus);
		btnProcesar.setEnabled(editStatus);
		btnLimpiar.setEnabled(!editStatus);
		if(editStatus){
			txtLadoA.requestFocus();
		}
	
	}


